import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import map from 'lodash/map';
import orderBy from 'lodash/orderBy';
import {
  Card,
  Grid,
  Button,
  TextField,
  Typography,
  CardContent,
} from '@material-ui/core';
import { searchAllPlanets } from '../../SWAPI';

const Home = () => {
  let history = useHistory();
  const signal = axios.CancelToken.source();
  const [searchValue, setSearchValue] = useState('');
  const [searchData, setSearchData] = useState([]);
  const [error, setError] = useState(false);

  useEffect(() => {
    document.title = `Login | Swapi App`;
    return () => {
      if (this.chart) {
        this.chart.dispose();
      }
      signal.cancel('Request Cancelled');
    };
  }, []);

  const processData = (data) => {
    const res = orderBy(data, ['population'], ['desc']);
    console.log({ res });
    return res;
  };

  const checkLogin = async (searchValue) => {
    try {
      const response = await searchAllPlanets(searchValue, signal.token);
      console.log(response);
      if (response && response.count > 0) {
        console.log('Search Successful', response.results);
        const data = processData(response.results);
        setSearchData([...response.results]);
      } else {
        setError(true);
        setSearchData([]);
      }
    } catch (error) {
      console.log('API Error', error);
    }
  };

  const handleSearch = (e) => {
    setSearchValue(e.target.value);
    checkLogin(e.target.value);
  };

  const handleLogout = () => {
    localStorage.clear();
    history.push('/login');
  };

  console.log(searchData);

  return (
    <>
      <Grid container spacing={2}>
        <Grid item xs={10}>
          <TextField
            type="search"
            id="standard-search"
            label="Search Field"
            placeholder="Type to Search"
            name="Search Field"
            fullWidth
            value={searchValue}
            onChange={handleSearch}
            required
            InputLabelProps={{
              shrink: true,
            }}
          />
        </Grid>
        <Grid item xs={2}>
          <Button
            variant="contained"
            color="primary"
            onClick={handleLogout}
            disabled={isLoggingOut}
          >
            Login
          </Button>
        </Grid>
        {map(searchData, (data, idx) => (
          <Grid key={idx + 1} item>
            <Card
              variant="outlined"
              style={{ width: '200px', height: '200px' }}
            >
              <CardContent>
                <Typography>{data.name}</Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </>
  );
};

export default Home;
