import React, { useState, useEffect } from 'react';
import axios from 'axios';
import map from 'lodash/map';
import { makeStyles } from '@material-ui/core/styles';
import { Grid, Card, CardContent, TextField } from '@material-ui/core';
import { searchAllPlanets } from '../../SWAPI';

const useStyles = makeStyles({
  root: {
    minWidth: 275,
  },

  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
});

const Home = () => {
  const classes = useStyles();
  const signal = axios.CancelToken.source();
  const [searchValue, setSearchValue] = useState('');
  const [searchData, setSearchData] = useState([]);
  const [error, setError] = useState(false);

  useEffect(() => {
    document.title = `Login | Swapi App`;
    return () => {
      if (this.chart) {
        this.chart.dispose();
      }
      signal.cancel('Request Cancelled');
    };
  }, []);

  const checkLogin = async (searchValue) => {
    try {
      const response = await searchAllPlanets(searchValue, signal.token);
      console.log(response);
      if (response && response.count > 0) {
        console.log('Search Successful', response.results);
        setSearchData([...response.results]);
      } else {
        setError(true);
        setSearchData([]);
      }
    } catch (error) {
      console.log('API Error', error);
    }
  };

  const handleSearch = (e) => {
    setSearchValue(e.target.value);
    checkLogin(e.target.value);
  };

  return (
    <>
      <Grid container spacing={2}>
        <Grid item xs={12} sm={12}>
          <TextField
            type="search"
            id="standard-search"
            label="Search Field"
            placeholder="Type to Search"
            name="Search Field"
            fullWidth
            value={searchValue}
            onChange={handleSearch}
            required
            InputLabelProps={{
              shrink: true,
            }}
          />
        </Grid>
        {map(searchData, (data, idx) => (
          <Grid key={idx + 1} item xs={3} sm={3}>
            <Card
              className={classes.root}
              style={{ width: '200px', height: '200px' }}
            >
              <CardContent>{data.name}</CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </>
  );
};

export default Home;
